INSERT INTO `implrvl_wc_admin_notes` VALUES("3","ecomm-need-help-setting-up-your-store","info","en_US","Need help setting up your Store?","Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.","{}","pending","woocommerce.com","2021-02-07 19:38:02","","0","plain","","0","info");
INSERT INTO `implrvl_wc_admin_notes` VALUES("4","woocommerce-services","info","en_US","WooCommerce Shipping & Tax","WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.","{}","pending","woocommerce.com","2021-02-07 19:38:02","","0","plain","","0","info");
INSERT INTO `implrvl_wc_admin_notes` VALUES("5","ecomm-unique-shopping-experience","info","en_US","For a shopping experience as unique as your customers","Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.","{}","pending","woocommerce.com","2021-02-07 19:38:02","","0","plain","","0","info");
INSERT INTO `implrvl_wc_admin_notes` VALUES("6","wcpay-promo-2020-11","marketing","en_US","Manage subscriber payments from your store\'s dashboard","Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>  – now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>","{}","pending","woocommerce.com","2021-02-07 19:38:02","","0","plain","","0","info");
